import {Box, Grid} from "@mui/material";
import "./Home.css";
import {Introduction} from "../components/Introduction";
import {Left} from "./Left";
import {Right} from "./Right";
import {useEffect, useState} from "react";
import {Education} from "../components/Education";
import {Experience} from "../components/Experience";
import {SkillHighlights} from "../components/SkillHighlights";
import {Projects} from "../components/Projects";
const Home = () => {
    const [mousePos, setMousePos] = useState({});
    const element = document.getElementsByClassName("main-div");

    useEffect(() => {
        const handleMouseMove = (event) => {
            setMousePos({ x: event.clientX, y: event.clientY });
        };

        window.addEventListener('mousemove', handleMouseMove);

        return () => {
            window.removeEventListener(
                'mousemove',
                handleMouseMove
            );
        };
    }, []);

    return (
        <div className={"main-div"}>
            <section id={"intro"} className={"intro"}>
                <div className={"intro-container"}>
                    <Introduction/>
                </div>
                <div className={"nav-container"}>
                    <div className={'nav-option'}>
                        <a href={"#education"}>Education</a>
                    </div>

                    <div className={'nav-option'}>
                        <a href={"#skill"}>Skill Highlights</a>
                    </div>

                    <div className={'nav-option'}>
                        <a href={"#experience"}>Experience</a>
                    </div>

                    <div className={'nav-option'}>
                        <a href={"#projects"}>Projects</a>
                    </div>
                </div>
            </section>

            <section id={"education"} className={"sections"}>
                <Education/>
            </section>

            <section id={"experience"} className={"sections"}>
                <Experience/>
            </section>

            <section id={"skill"} className={"sections"}>
                <SkillHighlights/>
            </section>

            <section id={"projects"} className={"sections"}>
                <Projects/>
            </section>
        </div>
    );
};

export default Home;
